// ------------------  汉字字模的数据结构定义 ------------------------ //
struct  typFNT_HZ16                 // 汉字字模数据结构 
{
       unsigned char  Index[3];               // 汉字内码索引        存放内码  如"硕"  但是一个字要两个字节表示
       unsigned char   Msk[32];                        // 点阵码数据       存放内码后对应的 点阵序列  每个字需要32个字节的点阵序列
};

struct  typFNT_HZ32                
{
       unsigned char  Index[3];             
       unsigned char   Msk[128];                      
};

